// SubscriptionPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../services/firebaseConfig';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import styled from 'styled-components';
import CloseButton from './ui/buttons/CloseButton';


const SubscriptionWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #f5f5f5;
`;

const SubscriptionForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 300px;
  padding: 20px;
  border: 1px solid #ddd;
  background: white;
  border-radius: 8px;
`;

const Input = styled.input`
  margin: 10px 0;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #0056b3;
  }
`;

const ErrorMessage = styled.p`
  color: red;
  font-size: 14px;
`;

const SubscriptionPage = () => {
  const navigate = useNavigate();
  const goToWelcomePage = () => {
    navigate('/');  // Redirige vers la page de connexion
  };
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubscription = async (e) => {
    e.preventDefault();
    try {
      // Créer un utilisateur avec email et mot de passe dans Firebase
      await createUserWithEmailAndPassword(auth, email, password);
      navigate('/home');  // Redirige vers la page principale après inscription
    } catch (err) {
      setError("Erreur d'inscription : " + err.message);
    }
  };

  return (
    <SubscriptionWrapper>
      <SubscriptionForm onSubmit={handleSubscription}>
        <CloseButton onClose={goToWelcomePage} />
        <h2>Inscription</h2>
        {error && <ErrorMessage>{error}</ErrorMessage>}
        <label>Email</label>
        <Input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <label>Mot de passe</label>
        <Input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <Button type="submit">S'inscrire</Button>
      </SubscriptionForm>
    </SubscriptionWrapper>
  );
};

export default SubscriptionPage;
