// src/context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';

// Création du contexte
const AuthContext = createContext();

// AuthProvider fournit l'état d'authentification aux composants enfants
export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    // Fonction de connexion
    const login = async (username, password) => {
        // Logique de connexion (par ex., appel API)
        const loggedInUser = { id: 1, name: 'John Doe', role: 'admin' };
        setUser(loggedInUser);
        localStorage.setItem('user', JSON.stringify(loggedInUser));
    };

    // Fonction de déconnexion
    const logout = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    // Chargement de l'utilisateur depuis le stockage local lors de l'initialisation
    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser) setUser(storedUser);
        setLoading(false);
    }, []);

    return (
        <AuthContext.Provider value={{ user, login, logout, loading }}>
            {children}
        </AuthContext.Provider>
    );
};

// Hook personnalisé pour accéder facilement au contexte d'authentification
export const useAuth = () => useContext(AuthContext);
