// my-gmao-app/src/App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [equipments, setEquipments] = useState([
    { id: 1, name: "Pompe à eau", status: "Fonctionnel" },
    { id: 2, name: "Compresseur d'air", status: "En panne" }
  ]);

  const [tickets, setTickets] = useState([
    { id: 1, issue: "Remplacer le filtre du compresseur", status: "Ouvert" },
    { id: 2, issue: "Vérifier la pression de la pompe à eau", status: "En cours" }
  ]);

  const [newEquipment, setNewEquipment] = useState({ name: "", status: "Fonctionnel" });

  const addEquipment = () => {
    setEquipments([...equipments, { id: equipments.length + 1, ...newEquipment }]);
    setNewEquipment({ name: "", status: "Fonctionnel" });
  };

  return (
    <div className="App">
      <h1>GMAO - Gestion de Maintenance</h1>

      <div className="section">
        <h2>Liste des équipements</h2>
        {equipments.map((equipment) => (
          <div key={equipment.id} className="equipment">
            <strong>{equipment.name}</strong> - {equipment.status}
          </div>
        ))}
      </div>

      <div className="section">
        <h2>Tickets de maintenance</h2>
        {tickets.map((ticket) => (
          <div key={ticket.id} className="ticket">
            <strong>Problème :</strong> {ticket.issue} <br />
            <strong>Statut :</strong> {ticket.status}
          </div>
        ))}
      </div>

      <div className="section">
        <h2>Ajouter un nouvel équipement</h2>
        <input
          type="text"
          placeholder="Nom de l'équipement"
          value={newEquipment.name}
          onChange={(e) => setNewEquipment({ ...newEquipment, name: e.target.value })}
        />
        <select
          value={newEquipment.status}
          onChange={(e) => setNewEquipment({ ...newEquipment, status: e.target.value })}
        >
          <option value="Fonctionnel">Fonctionnel</option>
          <option value="En panne">En panne</option>
        </select>
        <button onClick={addEquipment}>Ajouter</button>
      </div>
    </div>
  );
}

export default App;

