/* src/components/pages/LoginPage/LoginPage.js */
import React, { useState } from 'react';
import { auth } from '../../../services/FirebaseConfig';
import { signInWithEmailAndPassword, GoogleAuthProvider, signInWithPopup } from 'firebase/auth';
import styled from 'styled-components';
import { useNavigate, Link } from 'react-router-dom'
import CloseButton from '../../ui/buttons/CloseButton';
import { FaMicrosoft } from "react-icons/fa6";
import './LoginPage.css';

const LoginWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #f5f5f5;
`;

const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  width: 300px;
  padding: 20px;
  border: 1px solid #ddd;
  background: white;
  border-radius: 8px;
`;

const Input = styled.input`
  margin: 10px 0;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #0056b3;
  }
`;

const ErrorMessage = styled.p`
  color: red;
  font-size: 14px;
`;

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const goToWelcomePage = () => {
    navigate('/');  // Redirige vers la page de connexion
  };
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate('/home');// Rediriger vers la page principale après la connexion
    } catch (err) {
      setError("Erreur d'authentification : " + err.message);
    }
  };

  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
      navigate('/home');// Rediriger vers la page principale après la connexion
    } catch (err) {
      setError("Erreur d'authentification : " + err.message);
    }
  };

  return (
    <LoginWrapper>
      <LoginForm onSubmit={handleLogin}>
        <CloseButton onClose={'/'} />
        <h2>Connexion</h2>
        {error && <ErrorMessage>{error}</ErrorMessage>}
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <Input
          type="password"
          placeholder="Mot de passe"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <Button type="submit">Se connecter</Button>
        <Button type="button" onClick={handleGoogleLogin}>
          Se connecter avec Microsoft
          <FaMicrosoft size={40} color="blue" />
        </Button>
      </LoginForm>
      <Link to="/subscribe">Créer un compte</Link>
    </LoginWrapper>
  );
};

export default LoginPage;
