// src/components/Home.js
import React, { useState } from 'react';
import { useAuth } from './AuthContext';
import './HomePage.css';
import Header from './Header';
import Sidebar from './Sidebar';
import EquipmentsPage from './EquipmentsPage';
import TicketsPage from './TicketsPage';

export function HomePage() {
  const { logout } = useAuth();
  const [showSidebar, setShowSidebar] = useState(false);
  const [currentPage, setCurrentPage] = useState("home");
  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };
  const navigateTo = (page) => {
    setCurrentPage(page);
  };
  return (
    <div>
      <h1>Bienvenue sur le site</h1>
      <div className="app-container">
        <div className="ClassHeader">
          <button className="toggle-button" onClick={toggleSidebar}>
                 {showSidebar ? "Masquer le menu" : "Afficher le menu"}
          </button>
          <Header/>
        </div>
        {showSidebar && <Sidebar navigateTo={navigateTo} />}
        <div className={`content ${showSidebar ? 'with-sidebar' : 'full-width'}`}>
         {currentPage === "home" && <HomePage />}
         {currentPage === "equipments" && <EquipmentsPage />}
         {currentPage === "tickets" && <TicketsPage />}
        </div>
      </div>
      <button onClick={logout}>Se d√©connecter</button>
    </div>
  );
};
export default Home;
