// src/components/layout/Header/Header.js
import React, { useState } from 'react';
import { useAuth } from '../../../context/AuthContext';
import LogoutButton from '../../ui/buttons/LogoutButton';
import './Header.css';

function Header () {
    const { user } = useAuth();
    const [isMenuOpen, setMenuOpen] = useState(false);
    const [isUserInfoOpen, setUserInfoOpen] = useState(false);

    const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
    };

    const toggleUserInfo = () => {
    setUserInfoOpen(!isUserInfoOpen);
    };

    return (
        <header className="header">
            {user ? (
              <div>
                <button className="menu-button" onClick={toggleMenu}>☰</button>
                <nav className="nav">
                  <a href="#home" className="nav-link">Home</a>
                  <a href="#about" className="nav-link">About</a>
                  <a href="#services" className="nav-link">Services</a>
                  <a href="#contact" className="nav-link">Contact</a>
                </nav>
                <span>Bienvenue, {user.email}</span>
                <LogoutButton />
                <img 
                  src="https://via.placeholder.com/40" 
                  alt="Profile" 
                  className="profile-photo" 
                  onClick={toggleUserInfo} 
                />
                {isMenuOpen && <div className="sidebar-menu">Sidebar Menu Content</div>}
      {isUserInfoOpen && <div className="sidebar-user-info">User Information Content</div>}
              </div>


            ) : (
                <span>Vous n'êtes pas connecté</span>
            )}
        </header>
    );
};

export default Header;
