/* src/components/layout/SidebarMenu/SidebarMenu.js */
import React, { useState } from 'react';
import ButtonSideBar from '../../ui/buttons/ButtonSideBar';
import useSelectedModule from '../../../hooks/useSelectedModule';
import ModuleLayout from '../layouts/ModuleLayout';
import './SidebarMenu.css';

function SidebarMenu() {
  // État pour stocker le module sélectionné
  const [selectedModule, setSelectedModule] = useState(null);

  // Fonction pour changer de module
  const handleButtonClick = (module) => {
    setSelectedModule(module);
  };
  return (
    <div className="sidebar">
      <ul>
        <li>
          <ButtonSideBar label="Home" onClick={() => handleButtonClick('dashboard')}/>
        </li>
        <li>
          <ButtonSideBar label="Equipments" onClick={() => handleButtonClick('equipments')} />
        </li>
        <li>
          <ButtonSideBar label="Tickets" onClick={() => handleButtonClick('tickets')}/>
        </li>
      </ul>
      <div className="module-content">
        <ModuleContent selectedModule={selectedModule} />
      </div>
    </div>
  );
}

export default SidebarMenu;
